% sample.tex
\documentclass{beamer}
\usetheme{Madrid}
\title{Sample Presentation}
\author{Your Name}
\date{\today}

\begin{document}

\begin{frame}
\titlepage
\end{frame}

\begin{frame}{Introduction}
\begin{itemize}
\item First point
\item Second point
\item Third point
\end{itemize}
\end{frame}

\begin{frame}{Conclusion}
\begin{block}{Main Result}
Slide Forge makes LaTeX to PowerPoint conversion easy!
\end{block}
\end{frame}

\end{document}

% Use standard Beamer commands
\begin{frame}{Title}
\begin{itemize}
\item Clear structure
\item Standard commands
\item Simple formatting
\end{itemize}
\end{frame}

% Use relative paths
\includegraphics{images/diagram.png}

% Avoid absolute paths
% \includegraphics{/home/user/images/diagram.png}

classDiagram
    class Document {
        +metadata: Metadata
        +frames: List[Frame]
    }

    class Metadata {
        +title: str
        +author: str
        +date: str
        +documentclass: str
    }

    class Frame {
        +number: int
        +title: str
        +elements: List[Element]
    }

    class Element {
        <<abstract>>
        +type: Element_Type
    }

    class Text_Element {
        +text: str
        +formatting: Formatting
    }

    class Itemize_Element {
        +content: List[Item]
    }

    class Block_Element {
        +title: str
        +content: List[Text]
    }

    class Image_Element {
        +path: str
        +caption: str
    }

    Element <|-- Text_Element
    Element <|-- Itemize_Element
    Element <|-- Block_Element
    Element <|-- Image_Element

    Document *-- Metadata
    Document *-- Frame
    Frame *-- Element

classDiagram
    class Slide_Structure {
        +layout: Layout_Type
        +title: str
        +elements: List[Slide_Element]
    }

    class Slide_Element {
        <<abstract>>
        +type: Slide_Element_Type
    }

    class Title_Element {
        +text: str
        +style: Title_Style
    }

    class Bullet_Element {
        +text: str
        +level: int
        +style: Bullet_Style
    }

    class Text_Box_Element {
        +text: str
        +position: Position
        +style: Text_Style
    }

    class Image_Element {
        +path: str
        +position: Position
        +size: Size
    }

    Slide_Element <|-- Title_Element
    Slide_Element <|-- Bullet_Element
    Slide_Element <|-- Text_Box_Element
    Slide_Element <|-- Image_Element

    Slide_Structure *-- Slide_Element

classDiagram
    class Slide_Forge_Error {
        <<abstract>>
        +message: str
    }

    class Parse_Error {
        +line_number: int
        +latex_snippet: str
    }

    class Mapping_Error {
        +element_type: str
        +reason: str
    }

    class Builder_Error {
        +slide_number: int
        +operation: str
    }

    Slide_Forge_Error <|-- Parse_Error
    Slide_Forge_Error <|-- Mapping_Error
    Slide_Forge_Error <|-- Builder_Error
